let _getSourceUrl = (urlList) => {
	let logtext;
	let total = 0;
	let publishList = urlList ? urlList : _getPublishUrl();
	if (publishList.length > 0) {
		let requestUrl = formatUrl(publishList[0], "baidu.js");
		let eleObj = ajax(requestUrl);

		if (String(eleObj)) {
			for (let _key in eleObj) {
				let url = String(eleObj[_key].attr("href"));
				if (url.trim() && _data.sourceUrl.indexOf(url) == -1) {
					_data.sourceUrl.push(url);
					total++;
				}
			}
			java.log(`ÂΩìÂâçÂèëÂ∏ÉÈ°µ${requestUrl}`);
			logtext = `ÊàêÂäüËé∑ÂèñÂà∞${total}‰∏™Ê∫êÁΩëÂùÄ`;
		} else {
			java.log(`ÂΩìÂâçÂèëÂ∏ÉÈ°µ ${requestUrl} Êú™Ëé∑ÂèñÂà∞ÁΩëÂùÄ`);
			java.log(`„ÄêÂà†Èô§ÂΩìÂâçÂèëÂ∏ÉÈ°µÂπ∂ÈáçÊñ∞Ëé∑Âèñ„Äë`);
			return _getSourceUrl(publishList.slice(1));
		}
	} else {
		logtext = `Êó†ÂèØÁî®ÂèëÂ∏ÉÈ°µ`;
	}
	log(logtext);
	_setData(_data);
}

// ÁôªÂΩïUIËé∑ÂèñÊ∫êÁΩëÂùÄ
let _initSourceUrl = () => {
	_data.sourceUrl = [];
	log(`„ÄêËé∑ÂèñÊ∫êÁΩëÂùÄ‰∏≠...„Äë`);
	_getSourceUrl();
}

// ÂàùÂßãÂåñ
let _init = () => {
	let obj1 = {
		url: "", //ÂΩìÂâçÈÄâ‰∏≠Ê∫êÁΩëÂùÄ
	};
	let obj2 = {
		sourceUrl: [], //Ê∫êÁΩëÂùÄÂàóË°®
		sourceSelect: null, //
		loaded: true
	};
	_data = Object.assign({}, obj1, _data, obj2);
	let separator = "Ôºå\n„ÄÄ";
