st=source.getVariable();
st=st!=''?st:1;
a=java.getString('model.lines');
if(st==1){
	b=java.getString('model.illustration.imageUrl');
	b=b!=''?'<img src="'+b+'">':'';
	result=a+b
}else{result=a}

