ruleContent.nextContentUrl:
@js:
    if (result.match(/\/next\.png/)) {
        eval(result.match(/var jsarr =\[.*\];/)[0]);
        eval(result.match(/var jsstr =".*";/)[0]);
        jsstr = String(java.base64Decode(jsstr));

        var nnstr = "";
        var nnarr = [];
        for (a = 0; a < jsstr.length; a++) {
            nnarr.push(jsstr[a]);
        }
        for (b = 0; b < jsarr.length; b++) {
            nnstr += nnarr[jsarr[b]];
        }
        nnstr + ".html"
    }

