    let canvas = new Canvas(newImg);
    for (let i = 1; i <= num; i++) {
        let h = i === num ? remainder : 0;
        canvas.drawBitmap(Bitmap.createBitmap(img, 0, y * (i - 1), width, y + h), 0, height - y * i - h, null);
