ruleExplore.bookList:
@js:
  if(JSON.parse(src).code != 0){
    java.toast("请先登录账号！");
  }
  let list = [];
  let resultData = JSON.parse(src).data;
  let data;
  if(resultData.book_shelf_info){
    data = resultData.book_shelf_info;
    let requestData = {books: []};
    for(let i in data){
      requestData["books"].push({
        book_id: data[i]["book_id"],
        item_id: "0"
      });
    }
    let booksData = java.ajax("https://fanqienovel.com/api/bookshelf/multidetail,{method: \"post\",body: " + JSON.stringify(requestData) + "}");
    let books = JSON.parse(booksData).data.detail_list;
    for(let i in books){
      let d = books[i];
      list.push({
        src: d,
        book_name: d.book_name,
        thumb_url: d.thumb_url,
        abstract: d.abstract,
        url: `https://fanqienovel.com/page/${d.book_id}`
      });
    }
  } else if(resultData.book_list){
    data = resultData.book_list;
    function getCategoryName(category_tags){
      let a = [];
      for(let y in category_tags) a.push(category_tags[y].category_name);
      java.log(a)
      return a;
    }
    for(let i in data){
      let d = data[i];
      let e = JSON.parse(java.ajax(`https://novel.snssdk.com/api/novel/book/directory/list/v1?book_id=${d.bookId}`)).data.book_info;
      list.push({
        src: d,
        src2: e,
        author: e.author || "",
        book_name: e.book_name || d.bookName,
        thumb_url: e.thumb_url || d.thumbUri,
        abstract: e.abstract || d.abstract,
        word_number: e.word_number || d.wordName,
        lastChapterTitle: e.last_chapter_title || d.lastChapterTitle,
        url: `https://fanqienovel.com/page/${d.bookId}`
      });
    }
  }
  list
