exploreUrl:
@js:
url=source.getKey();
html=java.ajax(url);
a=org.jsoup.Jsoup.parse(html)
.select('.nav li a');
var sort=[];
if(html&&a){
	for(i in a){
		name=a[i].text()
		href=a[i].attr("href")
		link=String(href).replace(/1/,"{{page}}")
		add=`${name}::${link}`;
		sort.push(add)
		}
		sort.join("\n")
	}else{
		java.toast("发现列表获取失败！")
		}
