function headUrl(url){
        var countArray = url.split('https://www.84kanshu.com/xiaoshuo/');
        if(countArray[1]){
            let num=countArray[1].split('/')[0]
             return 'https://www.84kanshu.com/xiaoshuo/'+num+'/';
        }else{
            return url
        }
}

