		if(freetype==3)return;

		push('à¼ºË‡Â»`Êš'+$.freetype_name+'ÉÂ´Â«Ë‡à¼»', null, 1, freetype);
		$.categories.map($=>{
				category_id = $.category_id
				push('à¼º '+$.category_name+' à¼»', $$(freetype,category_id,sk,fk), 1, freetype);
				["ç»¼åˆ", "çƒ­é—¨", "æœ€æ–°"].map((title,sk)=>{
						sk++
						['['+title+']', 'å®Œç»“', 'è¿è½½'].map((title,fk)=>{
								fk++
