	var list = src
	.match(/chapterImages = (\[.+\])/)[1];
	var image = eval(list),
	head = `{"Referer": "${baseUrl}"}`;
	img = image.map(url => {
		if (!/^https?/.test(url) ) {
			//å¦‚æœé“¾æ¥ä¸å…¨ï¼Œé‡æ–°æ‹¼æ¥è·¯å¾„
			path=src.
			match(/chapterPath = "([^"]+)"/)||[];
			u="https://images.wszwhg.net/";
			p=path[1]||"";
			link=u+p;
			}else link="";
	c=` <img src="${link}${url},${head}"> `;
		return c;
		});
	img.join("\n")
	}catch(e){java.log(e)}

