    if (image.match(/^(\/r\/?)/i)) {
        return Cirh + image
    } else if (image.match(/^(\/|images?)/i)) {
        return Cih + image
    }
}
for(i in imgl){
url=get7ImageUrl(imgl[i]);
html+='<img src="'+url+'">\n'
}
