@js:
var list=[];
var bookSound=baseUrl.match(/(.*?\/\/.*?)\//)[1];//? ÈùûË¥™Â©™
if(result.indexOf("<script>alert")!=-1){
	//ÊêúÁ¥¢Âá∫Áé∞ÈóÆÈ¢ò,ÊÄª‰ºöÊúâÈóÆÈ¢òÁöÑ.ËΩØ‰ª∂ÈªòËÆ§‰∏ÄÊ¨°ÊêúÁ¥¢ËøõË°åÂ§öÊ¨°.ÁΩëÁ´ôËßÑÂÆö30ÁßíÊêúÁ¥¢1Ê¨°
}else{
	var data=result.match(/container flex flex-wrap mb20([^@]*)container"/)[1];
	var dataList=data.split("category-div");
	for(var i=1,len=dataList.length;i<len;i++){
		var temp=dataList[i].match(/flex flex-between commend-title"><a href="(.*)"><h3>([^@]*)<\/h3>.*<span>([^@]*)<\/span>[^@]*"intro indent">([^@]*?)<\/div>/);
		list.push({
			"name":temp[2],
			"author":temp[3],
			"intro":temp[4],
			"bookUrl":bookSound+temp[1]
		});
	}
}
list;

