function ckStore(ck) {
    this._value = ck;
    this.keyPairs = {};
    this.parse = () => {
        this._value.split(';').forEach(v => {
            kv = v.split('=').map(v => v.trim());
            this.keyPairs[kv[0]] = kv[1];
        });
        return this;
    }
    this.set = (k, v) => {
        this.keyPairs[k] = v;
        return this;
    };
    this.value = () => {
        res = void(0);
        for (k in this.keyPairs) {
            kv = `${k}=${this.keyPairs[k]};`;
            res = res === void(0) ?
                kv :
                res + kv;
        }
        return res;
    };
}
