ruleToc.chapterList:
@js:
let u = baseUrl.replace('list','content')

let v = [];
let list = [];
JSON.parse(result).data.list.map($=>{

//åˆ†å·åˆ¤å®š
		V = $.volumeTitle
		if(v[v.length-1]!=V&&!/^\s*$/.test(V)){
				v.push(V)
				list.push({
						name: 'ğŸ“–['+V+']ğŸ“–',
						volume: true
					})
			}

		return list.push({
				name: $.chapterName,
				url: u+'?chapterId='+$.chapterId+'&volumeId=1',
				info: 'æ›´æ–°æ—¶é—´ï¼š'+java.timeFormatUTC($.updateTime*1000,'yyyy-MM-dd HH:mm',12)
			});
	});
v.length<2?list.filter($=>!$.volume):list

ruleSearch.bookList:
 @css:ul > li<js>
result.toArray().slice(0, 3).map(p => {
	var url = (java.getElement("@css:body > div > ul > li > a.blue").attr("href"));
	var K = org.jsoup.Jsoup.parse(java.ajax(url));
	var n = selector => String(K.select(selector).text());
	var og = selector => String(K.select('[property="og:' + selector + '"]').attr('content'));
	return {
		name: og('novel:book_name'),
		author: og('novel:author'),
		kind: og('novel:category') + ',' + og('novel:status'),
		latest: og('novel:latest_chapter_name') + ' â€¢ ' + og('novel:update_time'),
		intro: og('description'),
		cover: og('image'),
		url: url,
	};
}); 
</js>

