@js:
try{
bUrl=baseUrl.replace(/\/?\#\#.*/,'');
cookie.removeCookie(bUrl)
s=String(java.ajax(bUrl))
act=s.match(/"act".*?"(.{16})"/)[1]
sub=s.match(/name="submit".*?value="(.*?)"/)[1]
body=String("act="+act+"&q="+key+"&submit="+sub)
xx=body.match(/[^&=]+/g)
for(i in xx){
re=java.encodeURI(xx[i],"gbk")
