@js:
comicId=result.match(/var comicId = (\d+)/)[1];
chapterId=result.match(/var chapterId=(\d+)/)[1];
nv=result.match(/var nv = "(.*?)"/)[1];
url='https://mm.sfacg.com/ajax/Common.ashx?op=getPics&cid='+comicId+'&chapId='+chapterId+'&serial=ZP&path='+nv
//java.ajax(url)
result=JSON.parse(java.ajax(url)).data
result.map(x=>'<img src=\"'+x+'\">').join("\n")

@js:
data=result.match(/data:\s*'(.*?)'/)[1];
nonce=result.match(/<script>window.*?=(.*?)<\/script>/)[1];
nonce=eval(nonce);
var N = String(nonce).match(/\d+\w+/);
jlen=N.length;
while(jlen){
jlen -= 1;
jlocate = parseInt(N[jlen].match(/(\d+)/)[0]) &255;
jstr = N[jlen].replace(/\d+/g,'');
data=data.substring(0,parseInt(jlocate))+data.substring(parseInt(jlocate)+jstr.length,data.length)}
piclist=eval(java.base64Decode(data).match(/"picture":(\[{".*\])/)[1]);
html='';
for(i in piclist){
html += '<img src="'+piclist[i].url+'">\n'
}
html

