@js:
var image = String(java.getElements('#ComicPic')); 
var url= image.match(/src=\"([^"]+)\"/)[1];
var start = parseInt( url.substring(url.lastIndexOf('/') + 1).split('.')[0] );
var total = start + parseInt(String(java.getElements('#total')).match(/value=\"([^"]+)\"/)[1]);
var html = image;
for(let i=start;i<total;i++){
	var img = image.replace(new RegExp(start+'',"g"),''+i);
	html += img;
}
result = html;

