		freetype = $.freetype
		if(freetype==3)return;

		push('‡º∫Àá¬ª` ö'+$.freetype_name+'…û¬¥¬´Àá‡ºª', null, 1, freetype);
		$.categories.map($=>{
				category_id = $.category_id
				push('‡º∫ '+$.category_name+' ‡ºª', $$(freetype,category_id,sk,fk), 1, freetype);
				["ÁªºÂêà", "ÁÉ≠Èó®", "ÊúÄÊñ∞"].map((title,sk)=>{
						sk++
						['['+title+']', 'ÂÆåÁªì', 'ËøûËΩΩ'].map((title,fk)=>{
								fk++
								push(title, $$(freetype,category_id,sk,fk), 0.25, freetype);
							});
					});
			});
	});
sort = sort1.concat(sort2);
cache.putFile('KuwoNovels', sort, 6E6);
}
'['+sort.toString()+']'

