}

let _setData = (obj) => {
	if (typeof obj == "object") {
		source.setVariable(JSON.stringify(obj));
	}
}

let _data = _getData();

let formatUrl = (string, replacement) => {
	let replacementText = '/';
	if (replacement) replacementText += replacement;
	return String(string).replace(/\/?$/, replacementText);
}

let ajax = (requestUrl) => {
	let result = java.ajax(requestUrl);
	// Ëé∑ÂèñÂèëÂ∏ÉÈ°µÈÄöËøá<iframe>Ê†áÁ≠æÂÜÖÂµåÁöÑÂèëÂ∏ÉÈ°µÁΩëÂùÄ https://xx/baidu.js
	let _requestUrl = org.jsoup.Jsoup.parse(result).select("iframe").attr("src");
	if (result == '404') {
		// ÂèëÂ∏ÉÈ°µÂèØÁõ¥Êé•ËÆøÈóÆÔºåËé∑ÂèñÂüüÂêç https://xx/
		result = java.ajax(requestUrl.replace(/(https?:\/\/.*?\/).*/, "$1"));
	} else if (_requestUrl != "") {
		//ÂèëÂ∏ÉÈ°µÈÄöËøá<iframe>Ê†áÁ≠æÂÜÖÂµåÁΩëÂùÄ
		result = java.ajax(_requestUrl);
	}
	return org.jsoup.Jsoup.parse(result).select(".line a");
}

//Ëé∑ÂèñÂèëÂ∏ÉÈ°µÂàóË°®
let _getPublishUrl = () => {
	return [source.key, "https://5s6s7s8s.com/", "https://qoqoqo.xyz/", "https://5m6m7m8m.com/"];
}

//Ëé∑ÂèñÊ∫êÁΩëÂùÄÂàóË°®
let _getSourceUrl = (urlList) => {
	let logtext;
	let total = 0;
	let publishList = urlList ? urlList : _getPublishUrl();
	if (publishList.length > 0) {
		let requestUrl = formatUrl(publishList[0], "baidu.js");
		let eleObj = ajax(requestUrl);

		if (String(eleObj)) {
			for (let _key in eleObj) {
				let url = String(eleObj[_key].attr("href"));
				if (url.trim() && _data.sourceUrl.indexOf(url) == -1) {
					_data.sourceUrl.push(url);
					total++;
				}
			}
			java.log(`ÂΩìÂâçÂèëÂ∏ÉÈ°µ${requestUrl}`);
			logtext = `ÊàêÂäüËé∑ÂèñÂà∞${total}‰∏™Ê∫êÁΩëÂùÄ`;
		} else {
			java.log(`ÂΩìÂâçÂèëÂ∏ÉÈ°µ ${requestUrl} Êú™Ëé∑ÂèñÂà∞ÁΩëÂùÄ`);
			java.log(`„ÄêÂà†Èô§ÂΩìÂâçÂèëÂ∏ÉÈ°µÂπ∂ÈáçÊñ∞Ëé∑Âèñ„Äë`);
			return _getSourceUrl(publishList.slice(1));
		}
	} else {
		logtext = `Êó†ÂèØÁî®ÂèëÂ∏ÉÈ°µ`;
	}
	log(logtext);
	_setData(_data);
}

// ÁôªÂΩïUIËé∑ÂèñÊ∫êÁΩëÂùÄ
let _initSourceUrl = () => {
	_data.sourceUrl = [];
	log(`„ÄêËé∑ÂèñÊ∫êÁΩëÂùÄ‰∏≠...„Äë`);
	_getSourceUrl();
}

// ÂàùÂßãÂåñ
let _init = () => {
	let obj1 = {
		url: "", //ÂΩìÂâçÈÄâ‰∏≠Ê∫êÁΩëÂùÄ
	};
	let obj2 = {
		sourceUrl: [], //Ê∫êÁΩëÂùÄÂàóË°®
		sourceSelect: null, //
		loaded: true
	};
	_data = Object.assign({}, obj1, _data, obj2);
	let separator = "Ôºå\n„ÄÄ";
	_initSourceUrl();
	java.log(_data.sourceUrl.join(separator));
	log(`„ÄêÈÄâÊã©ÈªòËÆ§Ê∫êÁΩëÂùÄ„Äë`);
	_selectSourceUrl(0);
}

let _selectSourceUrl = (index) => {
	try {
		if (_data.sourceUrl[index] == undefined) {
			log(`ÂΩìÂâçÈÄâÈ°πÊó†ÂèØÁî®Ê∫êÁΩëÂùÄ`);
		} else {
			_data.url = formatUrl(_data.sourceUrl[index]);
			log(`ÂàáÊç¢Ê∫êÁΩëÂùÄ‰∏∫${_data.url}`);
		}
	} catch (e) {
		log(`ÂΩìÂâçÈÄâÈ°πÊó†ÂèØÁî®Ê∫êÁΩëÂùÄ`);
	}
	_data.sourceSelect = null;
	_setData(_data);
}

let _select = (index) => {
	if (index == undefined) return;
	let title = `[ÈÄâÈ°π${(index + 1)}]`;
	let logtext = `Â∑≤ÈÄâÊã©${title}`;

	if (_data.sourceUrl[index] == undefined) {
		logtext = `${title}‰∏çÂèØ‰ΩøÁî®`;
		index = null;
	} else if (formatUrl(_data.sourceUrl[index]) == _data.url) {
		logtext = `${title}Ê∫êÁΩëÂùÄÂ∑≤Âú®‰ΩøÁî®`;
		index = null;
	} else if (_data.sourceSelect == index) {
		logtext = `Â∑≤ÂèñÊ∂àÈÄâÊã©${title}`;
		index = null;
	}

	_data.sourceSelect = index;
	log(logtext);
	_setData(_data);
}

let _cancel = () => {
	log(`Â∑≤ÂèñÊ∂àÈÄâÊã©ÁöÑÈÄâÈ°π`);
	_data.sourceSelect = null;
	_setData(_data);
}

let _confirm = () => {
	if (_data.sourceSelect == null || _data.sourceSelect == undefined) {
		return log(`ËØ∑ÈÄâÊã©‰ªªÊÑèÈÄâÈ°π`);
	} else if (_data.sourceUrl[_data.sourceSelect] == undefined) {
		log(`[ÈÄâÈ°π${_data.sourceSelect + 1}]‰∏çÂèØ‰ΩøÁî®`);
	} else if (formatUrl(_data.sourceUrl[_data.sourceSelect]) == _data.url) {
		log(`[ÈÄâÈ°π${_data.sourceSelect + 1}]Ê∫êÁΩëÂùÄÂ∑≤Âú®‰ΩøÁî®`);
	} else {
		return _selectSourceUrl(_data.sourceSelect);
	}
	_data.sourceSelect = null;
	_setData(_data);
}


//Ëé∑ÂèñÂΩìÂâçÂú∞ÂùÄ
let index = 0;
_getUrl = () => {
	if (index < 3) {
		index++;
		try {
			if (!_data.loaded || !_data.url) {
				//ÂàùÂßãÂåñ
				_init();
				return _getUrl();
			}
			return _data.url;
		} catch (err) {
			//ÂàùÂßãÂåñ
			_init();
			return _getUrl();
		}
	} else {
		java.toast("ÂàùÂßãÂåñÂ§±Ë¥•");
		return null;
	}
}

