  function stt(bArr) {
        if (bArr == null) {
            return null;
        }
        var stringBuffer = new StringBuffer();
        for (i=0;i< bArr.length;++i) {
            var hexString = Integer.toHexString(bArr[i] & 255);
            if (hexString.length() == 1) {
                hexString = '0' + hexString;
            }
            stringBuffer.append(hexString);
        }
        return stringBuffer.toString();
    };
    function thirdtoken(str) {
       var h= "{\"time\":" + Math.round(new Date()) + "}";

       var bytes = String(str).getBytes();
       var bytes2 = String(px2b).getBytes();
       var bytes3 = String(h).getBytes();

       var keyString = "0123456789ab";
       var bArr = String(keyString).getBytes();    
       new SecureRandom().nextBytes(bArr); 
       //java.log(bArr.length)      
       var secretKeySpec = new SecretKeySpec(bytes, "AES");
       var instance = Cipher.getInstance("AES/GCM/NoPadding");
       instance.init(1, secretKeySpec, new GCMParameterSpec(128, bArr));
       instance.updateAAD(bytes2);
       var doFinal = instance.doFinal(bytes3);
       if (doFinal == null) {
                return null;
        }
        var i = stt(bArr);
        var i2 = stt(doFinal);
        return px2b + ':' + i + i2;
